,"kind":"user","methods":{"IS_UNISWAP_VIEW()":{"notice":"Constant indicating that this contract is a UniswapView"},"add((address,bytes32,uint256,uint8,uint256,address,bool)[])":{"notice":"Add new asset(s)"},"admin()":{"notice":"Admin address"},"anchorPeriod()":{"notice":"The minimum amount of time in seconds required for the old uniswap price accumulator to be replaced"},"canAdminOverwrite()":{"notice":"Whether or not existing token configs can be overwritten"},"changeAdmin(address)":{"notice":"Changes the admin"},"ethBaseUnit()":{"notice":"The number of wei in 1 ETH"},"expScale()":{"notice":"A common scaling factor to maintain precision"},"getTokenConfig(uint256)":{"notice":"Get the i-th config, according to the order they were passed in originally"},"getTokenConfigByCToken(address)":{"notice":"Get the config for the cToken"},"getTokenConfigByUnderlying(address)":{"notice":"Get the config for an underlying asset"},"getUnderlyingPrice(address)":{"notice":"Get the underlying price of a cToken"},"isPublic()":{"notice":"If new token configs can be added by anyone"},"maxSecondsBeforePriceIsStale()":{"notice":"The maxmimum number of seconds elapsed since the price was last updated before it is considered stale. If set to 0, no limit is enforced."},"newObservations(address)":{"notice":"The new observation for each underlying"},"numTokens()":{"notice":"The number of tokens this contract actually supports"},"oldObservations(address)":{"notice":"The old observation for each underlying"},"postPrices(address[])":{"notice":"Update Uniswap TWAP prices"},"price(address)":{"notice":"Get the official price for an underlying token address"},"prices(address)":{"notice":"Official prices by underlying"}},"version":1}},"settings":{"compilationTarget":{"tests/contracts/MockUniswapView.sol":"MockUniswapView"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"contracts/Uniswap/UniswapConfig.sol":{"content":"// SPDX-License-Identifier: GPL-3.0

pragma solidity ^0.6.10;
pragma experimental ABIEncoderV2;

import { CToken, CErc20 } from "./UniswapLib.sol";

contract UniswapConfig {
    /// @dev Describe how to interpret the fixedPrice in the TokenConfig.
    enum PriceSource {
        FIXED_ETH, /// implies the fixedPrice is a constant multiple of the ETH price (which varies)
        FIXED_USD, /// implies the fixedPrice is a constant multiple of the USD price (which is 1)
        REPORTER,  /// implies the price is set by the reporter (only available on UniswapAnchoredView)
        TWAP,      /// implies the price is set by TWAPs (only available on UniswapView)
        UNISWAP    /// implies the price is set by the direct Uniswap price (only available on UniswapDirectView)
    }

    /// @dev Describe how the USD price should be determined for an asset.
    ///  There should be 1 TokenConfig object for each supported asset, passed in the constructor.
    struct TokenConfig {
        address underlying;
        bytes32 symbolHash;
        uint256 baseUnit;
        PriceSource priceSource;
        uint256 fixedPrice;
        address uniswapMarket;
        bool isUniswapReversed;
    }

    /// @dev Token config objects
    TokenConfig[] internal _configs;

    /// @dev Maps underlying addresses to token config indexes
    mapping(address => uint256) internal _configIndexesByUnderlying;

    /// @dev Maps underlying addresses to booleans indicating if they have token configs
    mapping(address => bool) internal _configPresenceByUnderlying;
    
    /// @notice Admin address
    address public admin;
    
    /// @notice Whether or not existing token configs can be overwritten
    bool public canAdminOverwrite;

    /// @notice The maxmimum number of seconds elapsed since the price was last updated before it is considered stale. If set to 0, no limit is enforced.
    uint256 public maxSecondsBeforePriceIsStale;

    /**
     * @notice Construct an immutable store of configs into the contract data
     * @param configs The configs for the supported assets
     * @param _canAdminOverwrite Whether or not existing token configs can be overwritten
     * @param _maxSecondsBeforePriceIsStale The maxmimum number of seconds elapsed since the price was last updated before it is considered stale. If set to 0, no limit is enforced.
     */
    constructor(TokenConfig[] memory configs, bool _canAdminOverwrite, uint256 _maxSecondsBeforePriceIsStale) public {
        // Initialize variables
        admin = msg.sender;
        canAdminOverwrite = _canAdminOverwrite;
        maxSecondsBeforePriceIsStale = _maxSecondsBeforePriceIsStale;

        // Add configs
        _add(configs);
    }

    /**
     * @dev Internal function to add new asset(s)
     * @param configs The static token configurations which define what prices are supported and how
     */
    function _add(TokenConfig[] memory configs) internal virtual {
        // For each config
        for (uint256 i = 0; i < configs.length; i++) {
            // If !canAdminOverwrite, check for existing config
            if (!canAdminOverwrite) require(!_configPresenceByUnderlying[configs[i].underlying], "Token config already exists for this underlying token address.");

            // Add config to state
            _configs.push(configs[i]);
            _configIndexesByUnderlying[configs[i].underlying] = _configs.length - 1;
            _configPresenceByUnderlying[configs[i].underlying] = true;
        }
    }

    /// @notice Changes the admin
    function changeAdmin(address newAdmin) external {
        require(msg.sender == admin, "msg.sender is not admin");
        admin = newAdmin;
    }

    /// @notice The number of tokens this contract actually supports
    function numTokens() public view returns (uint256) {
        return _configs.length;
    }

    /// @dev Get token config index by cToken address
    function getCTokenIndex(address cToken) internal view returns (uint) {
        return getUnderlyingIndex(CToken(cToken).isCEther() ? address(0) : CErc20(cToken).underlying());
    }

    /// @dev Get token config index by underlying ERC20 token address
    function getUnderlyingIndex(address underlying) internal view returns (uint) {
        return _configPresenceByUnderlying[underlying] ? _configIndexesByUnderlying[underlying] : uint(-1);
    }

    /**
     * @notice Get the i-th config, according to the order they were passed in originally
     * @param i The index of the config to get
     * @return The config object
     */
    function getTokenConfig(uint i) public view returns (TokenConfig memory) {
        require(i < numTokens(), "token config not found");
        return _configs[i];
    }

    /**
     * @notice Get the config for the cToken
     * @param cToken The address of the cToken of the config to get
     * @return The config object
     */
    function getTokenConfigByCToken(address cToken) public view returns (TokenConfig memory) {
        uint index = getCTokenIndex(cToken);
        if (index != uint(-1)) return getTokenConfig(index);
        revert("token config not found");
    }

    /**
     * @notice Get the config for an underlying asset
     * @param underlying The address of the underlying asset of the config to get
     * @return The config object
     */
    function getTokenConfigByUnderlying(address underlying) public view returns (TokenConfig memory) {
        uint index = getUnderlyingIndex(underlying);
        if (index != uint(-1)) return getTokenConfig(index);
        revert("token config not found");
    }
}
